mapper.select:

WHERE A.flag_use = 'Y'

        AND A.id = #{board_id}


        AND A.type = #{type}


        AND DATE_FORMAT(A.created_at, '%y-%m-%d') BETWEEN #{startSrchDate} AND #{endSrchDate}



ORDER BY A.updated_at DESC, A.created_at DESC

mapper.update:
UPDATE zarangzill.tb_board
SET
    editor = #{editor}
    , updated_at = sysdate()

    , subject = #{subject}


  , description = #{description}


  , type = #{type}


  , flag_push = #{flag_push}


  , flag_use = #{flag_use}

WHERE id =  #{board_id}

mapper.select:
SELECT A.id AS board_id , A.description AS board_description , A.flag_use AS board_flag_use , B.id AS channel_id , B.name AS channel_name , B.profile_image AS channel_profile_image , DATEDIFF(SYSDATE(), IFNULL(A.updated_at ,A.created_at)) AS date_diff
AND A.id = #{board_id}
AND B.id = #{channel_id}
AND C.user_id = #{user_id}

SELECT A.id AS board_id , A.subject , A.description AS board_description , A.flag_use AS board_flag_use , B.id AS channel_id , B.name AS channel_name , B.profile_image AS channel_profile_image , DATEDIFF(SYSDATE(), IFNULL(A.updated_at ,A.created_at)) AS date_diff , D.id AS reply_id , D.reply_description , D.flag_use AS reply_flag_use , D.creator AS reply_creator
Inner Join tb_board_reply D ON A.id = D.board_id WHERE D.creator = #{user_id} AND C.user_id = #{user_id}
AND A.id = #{board_id}
AND B.id = #{channel_id}
AND C.user_id = #{user_id}

SELECT A.id AS board_id , A.description AS board_description , A.flag_use AS board_flag_use , B.id AS channel_id , B.name AS channel_name , B.profile_image AS channel_profile_image , DATEDIFF(SYSDATE(), IFNULL(A.updated_at ,A.created_at)) AS date_diff
AND A.id = #{board_id}
AND B.id = #{channel_id}
AND C.user_id = #{user_id}
